var title_f11_54_12140="Intraarticular steroid Indix";
var content_f11_54_12140=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F62905&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F62905&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Indications and relative contraindications for intraarticular corticosteroid injection",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Indications",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Rheumatoid arthritis, persistent activity in large- or medium-sized joints in which injection may slow erosion",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Other inflammatory arthritides erosive or not, but particularly if oligoarticular, eg, Reiters, gout, psoriatic arthritis, other spondyloarthropathy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Osteoarthritis of large weightbearing joint (knee) with bone on bone pain and eventual total joint arthroplasty planned or accepted already",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Small joint osteoarthritis, eg, of the first carpometacarpal, especially with slight inflammatory component on physical exam",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Subdeltoid bursitis or rotator cuff tendonitis, first presentation or already investigated radiologically",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Relative contraindications",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Rheumatoid arthritis in small joints of hand in which soft tissue support is necessary to prevent deformity is subject to atrophy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Rheumatoid arthritis, one joint out of proportion to others; infection suspect, culture on first athrocentesis before injection",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Other monoarthritis suspected of sepsis, especially in diabetes",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Early osteoarthritis in which cartilage repair may be important",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Small joint osteoarthritis which failed to respond for six weeks to previous injection",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Subacute shoulder pain not responsive to previous injection",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
