var title_f11_54_12141="Routine PN preterm infant";
var content_f11_54_12141=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F81451&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F81451&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Composition of routine parenteral nutrition (PN) regimen for premature infants",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Component",
"       </td>",
"       <td class=\"subtitle1\">",
"        Concentration/additive",
"       </td>",
"       <td class=\"subtitle1\">",
"        Intake",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Glucose",
"       </td>",
"       <td>",
"        12.5 percent",
"       </td>",
"       <td>",
"        Glucose = 16 g/kg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Amino acids*",
"       </td>",
"       <td>",
"        2.6 percent",
"       </td>",
"       <td>",
"        Amino acids = 3.5 g/kg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cysteine-HCl",
"       </td>",
"       <td>",
"        30 mg/g amino acids",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        NaCl",
"       </td>",
"       <td>",
"        2.6 mmol = 2.6 mEq",
"       </td>",
"       <td>",
"        Na = 3.4 mmol/kg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        KCl",
"       </td>",
"       <td>",
"        0 to 0.2 mmol (mEq)",
"       </td>",
"       <td>",
"        Total K = 3.1 to 3.8 mmol/kg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        K",
"        <sub>",
"         2",
"        </sub>",
"        PO",
"        <sub>",
"         4",
"        </sub>",
"        -KHPO",
"        <sub>",
"         4",
"        </sub>",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        1.5 to 2.0 mmol P",
"       </td>",
"       <td>",
"        P = 2.0 to 2.6 mmol/kg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ca gluconate",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        1.5 to 2.0 mmol Ca",
"       </td>",
"       <td>",
"        Ca = 2.0 to 2.6 mmol/kg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        MgSO",
"        <sub>",
"         4",
"        </sub>",
"       </td>",
"       <td>",
"        0.25 mmol = 0.5 mEq",
"       </td>",
"       <td>",
"        Mg = 0.3 mmol/kg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Multivitamin-Pediatric&reg;",
"        <sup>",
"         &loz;",
"        </sup>",
"       </td>",
"       <td>",
"        40 percent of vial (2 mL/kg/day)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        MTE-4&reg;",
"        <sup>",
"         &sect;",
"        </sup>",
"       </td>",
"       <td>",
"        0.2 mL/kg/day",
"       </td>",
"       <td>",
"        See below for Zn, Cu, Mn, Cr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Zinc",
"       </td>",
"       <td>",
"        200 mcg/kg/day",
"       </td>",
"       <td>",
"        See total zinc below",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Selenium",
"       </td>",
"       <td>",
"        2 mcg/kg/day",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Heparin",
"       </td>",
"       <td>",
"        1 unit/mL",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Intralipid&reg; 20 percent",
"        <sup>",
"         &yen;",
"        </sup>",
"       </td>",
"       <td>",
"        15 mL/kg/day",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Total fluid",
"        </strong>",
"       </td>",
"       <td>",
"        <strong>",
"         150 mL/kg/day",
"        </strong>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Routine use in premature infants generally after five days of age who had received the early PN regimen.",
"    <div class=\"footnotes\">",
"     * TrophAmine&reg; (B. Braun).",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Monobasic-dibasic phosphate contains 1.4 mmol K/mmol P or 1.3 mmol Na/mmol P, depending upon the preparation used. Use the higher P concentration for PN duration &gt;2 weeks.",
"     <br/>",
"     &Delta; Use the higher concentration for PN duration &gt;2 weeks. Add last in the preparation of PN.",
"     <br/>",
"     <span class=\"lozenge\">",
"      &loz;",
"     </span>",
"     Multivitamin-Pediatric, Mayne Pharma (USA), Inc., Paramus, NJ.",
"     <br/>",
"     &sect; MTE-4, American Regent, Inc., Shirley, NY, provides:",
"     <br/>",
"     - Zn = 200 mcg/kg/day.",
"     <br/>",
"     - Cu = 20 mcg/kg/day.",
"     <br/>",
"     - Mn = 1 mcg/kg/day.",
"     <br/>",
"     - Cr = 0.4 mcg/kg/day.",
"     <br/>",
"     Total zinc = 200 + 200 = 400 mcg/kg/day.",
"     <br/>",
"     Usually begin MTE after two weeks; only Zn (400 mcg/kg/day) is needed prior to that time.",
"     <br/>",
"     &yen; Begin lipid at 1 g/kg/day and increase stepwise to 3 to 4 g/kg/day.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Schanler RJ. The low birth weight infant: Perinatal nutrition. In: Nutrition in Pediatrics: Basic Science and Clinical Applications, Walker WA, Watkins JB (Eds), B.C. Decker Inc., Hamilton, Ontario, Canada 1996. Copyright &copy;1996 PMPH-USA, Ltd.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
