" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Use of peritoneal dialysis for the treatment of acute kidney injury (acute renal failure)",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/56/12169/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?11/56/12169/contributors\">",
"     Thomas A Golper, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/56/12169/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
