var title_f11_57_12188="Recs breast MRI screen";
var content_f11_57_12188=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F54270&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F54270&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACS recommendations for breast MRI screening as an adjunct to mammography",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Recommend annual MRI screening (based on high risk of breast cancer and high sensitivity of MRI*)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <em>",
"        BRCA",
"       </em>",
"       mutation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       First-degree relative of",
"       <em>",
"        BRCA",
"       </em>",
"       carrier, but untested",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lifetime risk &gt;20-25 percent or greater, as defined by BRCAPRO or other models that are largely dependent on family history",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Recommend annual MRI screening (based on high risk of breast cancer)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Radiation to chest between age 10 and 30 years",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Li-Fraumeni syndrome and first-degree relatives",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cowden and Bannayan-Riley-Ruvalcaba syndromes and first-degree relatives",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Insufficient evidence to recommend for or against MRI screening&Delta;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lifetime risk 15-20 percent, as defined by BRCAPRO or other models that are largely dependent on family history",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lobular carcinoma in situ (LCIS) or atypical lobular hyperplasia (ALH)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Atypical ductal hyperplasia (ADH)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Heterogeneously or extremely dense breast on mammography",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Women with a personal history of breast cancer, including ductal carcinoma in situ (DCIS)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Recommend against MRI screening (based on expert consensus opinion)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Women at &lt;15 percent lifetime risk",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Evidence from nonrandomized screening trials and observational studies.",
"     <br>",
"      &Delta; Payment should not be a barrier. Screening decisions should be made on a case-by-case basis, as there may be particular factors to support MRI.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Saslow D, Boetes C, Burke W, et al. American Cancer Screening Guidelines for Breast Screening with MRI as an Adjunct to Mammography. CA Cancer J Clin 2007; 57:75. Copyright &copy; 2007 John Wiley &amp; Sons, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
