" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Course and treatment of chronic bifascicular block",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/62/12261/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?11/62/12261/contributors\">",
"     William H Sauer, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?11/62/12261/contributors\">",
"     Section Editor",
"    </a>",
