var title_f11_63_12284="Prev umbilical cath infection";
var content_f11_63_12284=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F69746&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F69746&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Prevention of infection with umbilical catheters",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Replacement and relocation of catheter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do not replace umbilical catheter if any sign of catheter-associated infection, vascular insufficiency, or thrombosis occurs.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Replace umbilical venous catheters only if mechanical malfunction of the catheter occurs.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Replacement of catheter-site dressing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Replace dressing when the catheter is removed or replaced, or when the dressing becomes damp, loosened, or soiled. Replace dressings more frequently in diaphoretic patients.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        There is no need to remove a gauze or opaque dressing if the patient has no clinical signs of infection.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Replacement of administration sets",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Replace administration sets, including secondary sets and add-on devices, no more frequently than at 72-hour intervals, unless clinically indicated.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Replace tubing used to administer blood, blood products, or lipid emulsions within 24 hours of initiating the infusion.",
"        <strong>",
"         No recommendation*",
"        </strong>",
"        for replacement of tubing used for intermittent infusions.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Replace tubing used to administer propofol infusions every 6 or 12 hours, depending on its use.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Hang time for parenteral fluids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Complete infusion of lipids or lipid-containing parenteral nutrition fluids (eg, 3-in-1 solutions) within 24 hours of hanging the fluid. When lipid emulsions are given alone, complete the infusion within 12 hours of hanging the emulsion.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Complete infusion of blood or blood products within four hours of hanging the blood.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         No recommendation*",
"        </strong>",
"        for the hang time of intravenous fluids, including nonlipid-containing parenteral nutrition fluids.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     *&nbsp;No recommendation is made when there is insufficient data in the literature.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
