var title_f11_63_12285="Long-term preventative tx of HAE";
var content_f11_63_12285=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F83115&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F83115&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Long-term prophylaxis to prevent angioedema in adults and children with hereditary angioedema (HAE)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Primary maintenance therapy",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Option 1",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Androgen therapy:",
"         </strong>",
"         May be started at a relatively high dose and gradually lowered once disease is controlled OR started at low dose and gradually increased until disease is controlled. Over time, the lowest effective dose should be determined for each individual patient.",
"        </p>",
"        <p>",
"         See additional treatments for breakthrough symptoms below.",
"        </p>",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Effective in most patients",
"         </li>",
"         <li>",
"          Well tolerated by many",
"         </li>",
"         <li>",
"          Inexpensive",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Side effects are prohibitive in some patients (elevated cholesterol, hypertension, mood effects, weight gain, others)",
"         </li>",
"         <li>",
"          Avoid in pre-pubertal children (premature closure of growth plate) and pregnant women (virilization of female fetus)",
"         </li>",
"         <li>",
"          Avoid in patients with liver disease or nephrotic syndrome",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Option 2",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Tranexamic acid therapy:",
"         </strong>",
"         Less effective than androgens, but preferred for pre-pubertal children and some women.",
"        </p>",
"        <p>",
"         See additional treatments for breakthrough symptoms below.",
"        </p>",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Safe in children",
"         </li>",
"         <li>",
"          Well tolerated by most patients",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Less effective compared&nbsp;with androgens",
"         </li>",
"         <li>",
"          Avoid in patients with history of thrombosis or thromboembolic disease or risk factor for thrombosis",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Option 3",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Regular infusions of a C1 inhibitor product:",
"         </strong>",
"        </p>",
"        <p>",
"         See additional treatments for breakthrough symptoms below.",
"        </p>",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Well tolerated",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Expensive",
"         </li>",
"         <li>",
"          Requires reliable venous access",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        <p>",
"         <strong>",
"          Treatment for breakthrough symptoms",
"         </strong>",
"        </p>",
"        <p>",
"         <em>",
"          (Required in combination with any of the options above)",
"         </em>",
"        </p>",
"       </td>",
"       <td>",
"        A C1 inhibitor replacement product",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Requires venous access",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Ecallantide",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Subcutaneous injection",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Should be given with supervision due to possible hypersensitivity reactions usually within one hour",
"         </li>",
"         <li>",
"          Only available in the United States",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Icatibant",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Subcutaneous injection",
"         </li>",
"         <li>",
"          Approved for self-administration at home",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Plasma (solvent/detergent treated&nbsp;[preferred]&nbsp;or fresh frozen)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Efficacy not as well established",
"         </li>",
"         <li>",
"          Theoretical risk of transmission of blood borne agents",
"         </li>",
"         <li>",
"          Requires visit to hospital or clinic for administration",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
