var title_f11_7_11388="Lifestyle changes in CVS";
var content_f11_7_11388=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F77478&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F77478&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Lifestyle changes in CVS",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Lifestyle changes (for one to two months or one to two cycles)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <strong>",
"         Reassurance",
"        </strong>",
"        (eg, episodes are not self-induced) and anticipatory guidance (eg, natural history)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <strong>",
"         Avoidance of triggers",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Keep a \"vomiting diary\" of potential precipitating factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Avoid fasting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Recognize the potential role of excitement as a trigger (eg, downplay big events)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Maintain good sleep hygiene (eg, avoid sleep deprivation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Avoid triggering foods: chocolate, cheese, monosodium glutamate, antigenic foods",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Avoid excessive energy output",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <strong>",
"         Supplemental carbohydrate: for fasting-induced episodes",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Provide fruit juices, other sugar-containing drinks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Provide extra snacks between meals, before exertion, or at bedtime",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <strong>",
"         Migraine headache lifestyle interventions",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Regular aerobic exercise (avoid overexercising)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Regular meal schedules (ie, avoid skipping meals)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Moderation in consuming or avoidance of caffeine",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Li BU, Lefevre F, Chelimsky GG, et al. North American Society for Pediatric Gastroenterology, Hepatology, and Nutrition consensus statement on the diagnosis and management of cyclic vomiting syndrome. J Pediatr Gastroenterol Nutr 2008; 47:379. Copyright &copy; 2008 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
