var title_f11_9_11419="Rome criteria for IBS";
var content_f11_9_11419=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F54997&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F54997&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Rome III diagnostic criteria* for irritable bowel syndrome",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Recurrent abdominal pain or discomfort",
"        <sup>",
"         &bull;",
"        </sup>",
"        at least 3 days per month in the last 3 months associated with 2 or more of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        (1) Improvement with defecation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        (2) Onset associated with a change in frequency of stool",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        (3) Onset associated with a change in form (appearance) of stool",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Criteria fulfilled for the last 3 months with symptom onset at least 6 months prior to diagnosis.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Discomfort means an uncomfortable sensation not described as pain. In pathophysiology research and clinical trials, a pain/discomfort frequency of at least 2 days a week during screening evaluation for subject eligibility.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Longstreth GF, Thompson WG, Chey WD, et al. Functional bowel disorders. Gastroenterology 2006; 130:1480.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
