var title_f12_0_12302="Drugs for GERD in children";
var content_f12_0_12302=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F55435&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F55435&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Drugs demonstrated to be effective for gastroesophageal reflux disease in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type of medication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommended oral dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Adverse effects/precautions*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Useful dose forms for children",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Proton pump inhibitors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Omeprazole",
"       </td>",
"       <td>",
"        <p>",
"         1.0 mg/kg/day divided into one or two doses daily",
"        </p>",
"        <p>",
"         (Adult dose 20 mg once daily)",
"        </p>",
"       </td>",
"       <td>",
"        Headache, diarrhea, abdominal pain, nausea, rash, constipation, vitamin B12 deficiency",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        Capsules can be opened and sprinkled on food",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Esomeprazole",
"       </td>",
"       <td>",
"        <p>",
"         1 month to 1 year (daily):",
"        </p>",
"        <p>",
"         &nbsp; 3 to 5 kg: 2.5 mg",
"        </p>",
"        <p>",
"         &nbsp; 5 to 7.5 kg: 5 mg",
"        </p>",
"        <p>",
"         &nbsp; 7.5 to 12 kg: 10 mg",
"        </p>",
"        <p>",
"         &nbsp;",
"        </p>",
"        <p>",
"         1 to 11 years (daily):",
"        </p>",
"        <p>",
"         &nbsp; weight &lt;20 kg: 10 mg",
"        </p>",
"        <p>",
"         &nbsp; weight &gt;20 kg: 10&nbsp;mg or&nbsp;20 mg",
"        </p>",
"        <p>",
"         &nbsp;",
"        </p>",
"        <p>",
"         Adult dose: 20 or 40 mg daily",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Same as above",
"         <sup>",
"          &bull;",
"         </sup>",
"        </p>",
"        <p>",
"         Indication in infants is for erosive esophagitis.",
"        </p>",
"       </td>",
"       <td>",
"        Available as granules for oral suspension, in 2.5, 5, 10, 20 and 40 mg packets.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"4\">",
"        Lansoprazole",
"       </td>",
"       <td rowspan=\"4\">",
"        <p>",
"         0.75-1.5 mg/kg/day",
"        </p>",
"        <p>",
"         (Adult dose: 15-30 mg once daily)",
"        </p>",
"        <p>",
"         IV dose same as oral dose",
"        </p>",
"       </td>",
"       <td rowspan=\"4\">",
"        Headache, diarrhea, abdominal pain, nausea, elevated transaminase, proteinuria, angina, hypotension",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        Granules for oral suspension (Prevacid&reg;)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dissolving tablet (Prevacid&reg; Solutab&reg;)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Capsules can be opened and sprinkled on food",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Pantoprazole",
"       </td>",
"       <td rowspan=\"2\">",
"        <p>",
"         No pediatric dose available",
"        </p>",
"        <p>",
"         (Adult dose: 40 mg once daily)",
"        </p>",
"        <p>",
"         IV dose same as oral dose (adults)",
"        </p>",
"       </td>",
"       <td rowspan=\"2\">",
"        Headache, diarrhea, abdominal pain, nausea",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        Oral tablets (Protonix&reg;)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rabeprazole",
"       </td>",
"       <td>",
"        <p>",
"         No pediatric dose available",
"        </p>",
"        <p>",
"         (Adult dose: 20 mg once daily)",
"        </p>",
"       </td>",
"       <td>",
"        Similar to other PPIs",
"       </td>",
"       <td>",
"        Oral tablets (AcipHex&reg;)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Omeprazole and sodium bicarbonate",
"       </td>",
"       <td>",
"        Similar to omeprazole",
"       </td>",
"       <td>",
"        Similar to other PPIs; also avoid using in patients on sodium restricted diets due to high content of sodium bicarbonate",
"       </td>",
"       <td>",
"        Oral capsules (Zegerid&reg; capsules), which should not be opened; powder for oral suspension (Zegerid&reg; powder), which should be dissolved in water",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Histamine",
"        <sub>",
"         2",
"        </sub>",
"        receptor antagonists",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cimetidine",
"       </td>",
"       <td>",
"        <p>",
"         40 mg/kg/day divided into three or four doses daily",
"        </p>",
"        <p>",
"         (Adult dose: 800-1200 mg/dose two to three times daily)",
"        </p>",
"       </td>",
"       <td>",
"        Rash, bradycardia, dizziness, nausea, vomiting, hypotension, gynecomastia, reduces hepatic metabolism of theophylline and other medications, neutropenia, thrombocytopenia, agranulocytosis, doses should be decreased with renal insufficiency",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        Liquid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nizatidine",
"       </td>",
"       <td>",
"        <p>",
"         10 mg/kg/day divided into two doses daily",
"        </p>",
"        <p>",
"         (Adult dose: 150 mg/dose twice daily or 300 mg once daily at bedtime)",
"        </p>",
"       </td>",
"       <td>",
"        Headache, dizziness, constipation, diarrhea, nausea, anemia, urticaria, doses should be decreased with renal insufficiency",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        Solution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ranitidine",
"       </td>",
"       <td>",
"        <p>",
"         5 to 10 mg/kg/day divided into three doses daily",
"        </p>",
"        <p>",
"         (Adult dose; 300 mg/dose twice daily)",
"        </p>",
"       </td>",
"       <td>",
"        Headache, dizziness, fatigue, irritability, rash, constipation, diarrhea, thrombocytopenia, elevated transaminases, doses should be decreased with renal insufficiency",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        Liquid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Famotidine",
"       </td>",
"       <td rowspan=\"2\">",
"        <p>",
"         1 mg/kg/day divided into two doses daily",
"        </p>",
"        <p>",
"         (Adult dose: 20 mg/dose twice daily)",
"        </p>",
"       </td>",
"       <td rowspan=\"2\">",
"        Headache, dizziness, constipation, diarrhea, nausea, doses should be decreased with renal insufficiency",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        Powder for suspension (Pepcid&reg;)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chewable tablet (Pepcid AC&reg;)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Prokinetic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"3\">",
"        Cisapride",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td rowspan=\"3\">",
"        <p>",
"         0.8 mg/kg/day divided into four doses daily",
"        </p>",
"        <p>",
"         (Adult dose: 10-20 mg/dose four times daily)",
"        </p>",
"       </td>",
"       <td>",
"        <span class=\"red\">",
"         Rare cases of serious cardiac arrhythmia (FDA recommends ECG before administration)",
"        </span>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span class=\"red\">",
"         Beware of drug interactions (macrolide antibiotics, imidazoles, protease inhibitors)",
"        </span>",
"       </td>",
"       <td>",
"        Very restricted use in the US",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span class=\"red\">",
"         Do not use in patients with liver, cardiac, orelectrolyte abnormalities (FDA recommends checking K+, Ca++, Mg++ and creatine before administration)",
"        </span>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     QD: once daily; BID: twice daily; TID: three times daily; QID: four times daily; QHS: once at bedtime.",
"     <br>",
"      * See individual drug topics for complete discussion of adverse effects.",
"      <br>",
"       <sup>",
"        &bull;",
"       </sup>",
"       Probable slightly increased risk for community-aquired pneumonia and enteric infections.",
"       <br>",
"        &Delta; Not available in many countries because of safety concerns.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        Rudolf CD, Mazur LJ, Liptak GS, et al. Guidelines for evaluation and treatment of gastroesophageal reflux in infants and children: recommendations of the North American Society for Pediatric Gastroenterology and Nutrition. J Pediatr Gastroenterol Nutr 2001; 32 Suppl 2:S1.",
"       </li>",
"       <li>",
"        Gibbons TE, Gold BD. The use of proton pump inhibitors in children: a comprehensive review. Paediatr Drug 2003; 5:25.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
