var title_f12_1_12316="Rx relapsed refractory FL";
var content_f12_1_12316=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F77636&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F77636&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of relapsed or refractory follicular lymphoma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        First relapse",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Observation*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clinical trial, if available",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Monoclonal antibody-based therapy (R)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Radioimmunotherapy&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chemotherapy with monoclonal antibody-based therapy (eg, R-CVP,&nbsp;R-CHOP, BR), depending on initial therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High dose chemotherapy followed by autologous or allogeneic HCT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Second or greater relapse",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Single agent or combination chemotherapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Monoclonal antibody-based therapy (R)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Radioimmunotherapy&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High dose chemotherapy followed by autologous or allogeneic HCT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Widespread disease, multiple prior therapies, transformed disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anthracycline-based combination chemotherapy (eg, CHOP) if not previously given",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Radioimmunotherapy&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Allogeneic or autologous HCT for chemoresponsive disease",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     R: rituximab; CVP: cyclophosphamide, vincristine, prednisone; CHOP: cyclophosphamide, doxorubicin, vincristine, prednisone; BR: bendamustine, rituximab; HCT: hematopoietic cell transplantation; .",
"     <br>",
"      *",
"      <strong>",
"       Indications for treatment",
"      </strong>",
"      include presence of \"B\" symptoms, threatened end-organ function, cytopenias, massive tumor bulk, and/or rapid progression. Patients with none of these signs or symptoms may be observed without an adverse effect on overall survival or disease transformation.",
"      <br>",
"       &bull; Radioimmunotherapy includes treatment with Iodine-131-labeled tositumomab or Yttrium-90-labeled ibritumomab.",
"       <br>",
"        Participation in a clinical trial is always recommended as a first option for all patients with follicular lymphoma.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
