"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Lymphatic filariasis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Onchocerciasis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Loiasis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Treatment regimen of choice",
"       </td>",
"       <td class=\"subtitle1\">",
