var title_f12_12_12494="Recommended scrn over 65 yrs";
var content_f12_12_12494=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F75220&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F75220&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of screening and counseling recommendations for adults age &ge;65 years",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Priority problem",
"       </td>",
"       <td class=\"subtitle1\">",
"        Brief recommendation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Historical information and counseling",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Exercise",
"       </td>",
"       <td>",
"        <p>",
"         Moderate to vigorous aerobic activity 3-5 times per week",
"        </p>",
"        <p>",
"         Weight training or resistance exercises to maintain strength",
"        </p>",
"        <p>",
"         Flexibility activities to maintain range of motion",
"        </p>",
"        <p>",
"         Balance training to improve stability and prevent falls",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Alcohol use",
"       </td>",
"       <td>",
"        <p>",
"         CAGE questionnaire",
"        </p>",
"        <p>",
"         Counseling to stop drinking",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tobacco use",
"       </td>",
"       <td>",
"        <p>",
"         Ongoing regular counseling to stop smoking",
"        </p>",
"        <p>",
"         Consideration of pharmacotherapy",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"4\">",
"        Medication use",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Regular review of medication list for:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Completeness, accuracy, adherence, and affordability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Drug-drug, drug-disease interactions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Careful attention to use of specific drug types/classes including warfarin, digoxin, antidiabetic, analgesic, antihypertensive, psychotropic, and anticholinergic drugs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Urinary incontinence (UI)",
"       </td>",
"       <td>",
"        <p>",
"         Inquire about presence and severity biannually",
"        </p>",
"        <p>",
"         Presence of UI should trigger medication review, GU exam, appropriate blood and urine tests",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Driving",
"       </td>",
"       <td>",
"        <p>",
"         Consideration of driving problems in those with problems with vision, mobility or cognition",
"        </p>",
"        <p>",
"         For demented patients, recommend stop driving or refer for detailed driving assessment",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Social support",
"       </td>",
"       <td>",
"        Regular screening for financial and social support",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Elder mistreatment",
"       </td>",
"       <td>",
"        Routine direct questioning about problems with abuse or neglect",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Advance directives",
"       </td>",
"       <td>",
"        Discussion and documentation of preferences with living will and designation of health care power-of-attorney",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Physical examination and testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Blood pressure",
"       </td>",
"       <td>",
"        <p>",
"         Measure every 1-2 years",
"        </p>",
"        <p>",
"         If treatment initiated, monitor orthostatic blood pressure, renal function and electrolytes",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Weight",
"       </td>",
"       <td>",
"        Weight loss of 10 percent or more per year triggers assessment of undernutrition, possible medical or medication-related causes, dental status, food security, food related functional status, appetite and intake, swallow ability and previous dietary restrictions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hearing and vision",
"       </td>",
"       <td>",
"        <p>",
"         Annual screening for hearing loss with patient inquiry and exam (Whisper test or handheld audiometry)",
"        </p>",
"        <p>",
"         General ophthalmologic examination, including screening for glaucoma, every 1-2 years",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cognition",
"       </td>",
"       <td>",
"        Targeted screening in patients with memory complaints or new functional impairment with MMSE, Mini-Cog, Clock Drawing Test, or Memory Impairment Screen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"3\">",
"        Mood",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Screen all older adults for depression with two questions:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        1) Feeling depressed or sad?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        2) Loss of interest?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Gait and balance",
"       </td>",
"       <td>",
"        Get Up and Go Test",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lipids",
"       </td>",
"       <td>",
"        Screen and treat older adults with CHD risk exceeding 10 percent over ten years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bone density",
"       </td>",
"       <td>",
"        Screening densitometry for osteoporosis for women beginning at age 65",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Abdominal aortic aneurysm",
"       </td>",
"       <td>",
"        One time screening ultrasound in men aged 65-75 with any history of smoking or family history of AAA requiring repair",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Cancer screening",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"4\">",
"        Cancer screening",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Key considerations in older adults:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Life expectancy: will this patient live long enough to benefit?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Potential harms: procedural complications, anxiety, cost and overdiagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Individual patient preference",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Breast cancer",
"       </td>",
"       <td>",
"        Screening mammography every 1-2 years as long as life expectancy at least 5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Colorectal cancer",
"       </td>",
"       <td>",
"        <p>",
"         Annual FOBT v.",
"        </p>",
"        <p>",
"         Screening colonoscopy every 10 years v.",
"        </p>",
"        <p>",
"         Flexible sigmoidoscopy every 5 years as long as life expectancy at least 5 years",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cervical cancer",
"       </td>",
"       <td>",
"        <p>",
"         May safely discontinue Pap smears at or after age 65 after 3 consecutive normals within 10 year period",
"        </p>",
"        <p>",
"         May discontinue after hysterectomy for benign indication",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Immunization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tetanus-diptheria vaccine",
"       </td>",
"       <td>",
"        Booster every 10 years in patients who have received primary series (alternative: booster once after age 50)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Influenza vaccine",
"       </td>",
"       <td>",
"        Annual vaccination after age 65",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pneumococcal vaccine",
"       </td>",
"       <td>",
"        <p>",
"         Vaccinate once after age 65",
"        </p>",
"        <p>",
"         Revaccinate once after age 65 if an initial vaccination was given before age 65 and five years have elapsed since the first dose",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Herpes zoster vaccine",
"       </td>",
"       <td>",
"        One time vaccination after age 60",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aspirin",
"       </td>",
"       <td>",
"        <p>",
"         Consider daily aspirin in patients with 5 year CHD risk of 3 percent or greater",
"        </p>",
"        <p>",
"         Weigh risks of gastrointestinal bleeding",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Calcium and Vitamin D",
"       </td>",
"       <td>",
"        1200 mg of elemental calcium (diet and/or supplement) and at least 800 IU of Vitamin D",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
