"   <form action=\"\" id=\"InFlightPaO2PFTs_form\" name=\"InFlightPaO2PFTs_form\" onkeydown=\"clrResults();\" onkeyup=\"InFlightPaO2PFTs_fx();\" onsubmit=\"return false;\">",
"    <table cellpadding=\"4\" cellspacing=\"0\" summary=\"MedCalc 3000 Table\" width=\"100%\">",
"     <tr>",
"      <td bgcolor=\"#eeeeee\" width=\"1%\">",
"       <br/>",
"      </td>",
"      <td align=\"left\" bgcolor=\"#eeeeee\">",
"       <span class=\"medCalcFontTitleBox\">",
"        Calculator: In-Flight PaO2 Estimation (using PFT's)",
"       </span>",
"      </td>",
"     </tr>",
"    </table>",
"    <br/>",
"    &nbsp;",
"    <br/>",
"    <center>",
"     <table cellpadding=\"10\" cellspacing=\"0\" summary=\"MedCalc 3000 Table\">",
"      <tr>",
"       <td align=\"left\" bgcolor=\"#6c9a9c\">",
"        <span class=\"medCalcFontFormuli\">",
"         PaO2 = 22.26 + (0.238 * SeaLevelPaO2) + (20.1 * FEV1 / FVC)",
"        </span>",
"       </td>",
"      </tr>",
"     </table>",
"     <br/>",
"     &nbsp;",
"     <br/>",
