var script_f12_13_12497=[" /* <![CDATA[ */",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function InFlightPaO2PFTs_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.InFlightPaO2PFTs_form){",
"",
"",
"doCalc = true;",
"if (Sea_Level_PaO2_param.value.indexOf(',') >= 0){ Sea_Level_PaO2_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Sea_Level_PaO2_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Sea_Level_PaO2_unit.options[Sea_Level_PaO2_unit.selectedIndex].value.split('|');",
"Sea_Level_PaO2 = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (FEV1_param.value.indexOf(',') >= 0){ FEV1_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(FEV1_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = FEV1_unit.options[FEV1_unit.selectedIndex].value.split('|');",
"FEV1 = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (FVC_param.value.indexOf(',') >= 0){ FVC_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(FVC_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = FVC_unit.options[FVC_unit.selectedIndex].value.split('|');",
"FVC = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"PaO2 =  22.26 + (0.238 * Sea_Level_PaO2) + (20.1 * FEV1 / FVC);",
"",
"unit_parts = PaO2_unit.options[PaO2_unit.selectedIndex].value.split('|');",
"if (doCalc) PaO2_param.value = fixDP((PaO2 - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.InFlightPaO2PFTs_form){",
"",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.InFlightPaO2PFTs_form){",
"",
"PaO2_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"/* ]]> */"].join("\n");
