var title_f12_14_12523="Cryptosporidium infection prevention in CD40L deficiency";
var content_f12_14_12523=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F86095&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F86095&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Strategies to prevent Cryptosporidium infection in patients with CD40L deficiency",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Drinking water should be boiled and/or filtered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Avoid swimming in pools, lakes, and ponds",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Avoid contact with farm animals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prophylactic use of azithromycin may reduce the risk of infection&nbsp;and is usually indicated, but its long-term efficacy in preventing Cryptosporidium infection is not known",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In addition, the following surveillance studies should be done every 6 to 12 months:",
"        <ul>",
"         <li>",
"          Test the stools by polymerase chain reaction (PCR) for the presence of Cryptosporidium",
"         </li>",
"         <li>",
"          Perform liver function tests (aspartate aminotransferase [AST], alanine aminotransferase [ALT], gamma-glutamyl transpeptidase [GGT], alkaline phosphatase, bilirubin)",
"         </li>",
"         <li>",
"          Liver ultrasound",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
