var title_f12_16_12555="Testing in diabetic pregnancy";
var content_f12_16_12555=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F51463&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F51463&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Frequency of testing during pregnancy in women with pregestatonal diabetes",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Test",
"       </td>",
"       <td class=\"subtitle1\">",
"        Frequency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hemoglobin A1C",
"       </td>",
"       <td>",
"        Every 4 to 6 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Blood glucose",
"       </td>",
"       <td>",
"        Home measurements 4 to 8 times daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urine ketones",
"       </td>",
"       <td>",
"        During period of illness; when any blood glucose value is &gt;200 mg/dL (11.1 mmol/L)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urine protein",
"       </td>",
"       <td>",
"        Diptstick at office visits, quantitate 24 hour excretion each trimester in women with nephropathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serum creatinine",
"       </td>",
"       <td>",
"        Each trimester in women with nephropathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thyroid function tests",
"       </td>",
"       <td>",
"        Baseline measurements of serum free T4 and TSH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eye examination",
"       </td>",
"       <td>",
"        Baseline and then as necessary per retinal specialist",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
