var title_f12_22_12651="Triggers autonomic dysreflexia";
var content_f12_22_12651=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F51824&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F51824&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Triggers of autonomic dysreflexia in pregnancy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <strong>",
"         Pelvis:",
"        </strong>",
"        manipulation of the vagina or cervix, bimanual examination, insertion of a vaginal speculum, swabbing the cervix or posterior fornix, or uterine contractions.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Genitourinary:",
"        </strong>",
"        manipulation of urinary catheters, catheter insertion, catheter obstruction, vesico-ureteral reflux, or renal colic.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Gastrointestinal:",
"        </strong>",
"        constipation, biliary colic, various gastrointestinal pathologies.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Dermatologic:",
"        </strong>",
"        decubitus ulcers, temperature variation below the level of the spinal cord lesion such as immersion of feet in cold or hot water, or placement of feet in cold stirrups.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Other:",
"        </strong>",
"        sexual activity",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Pereira, L. Obstet Gynecol Surv 2003; 58:678.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
