var title_f12_26_12717="Learning disability exam";
var content_f12_26_12717=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F53481&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F53481&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Important aspects of the examination of a child with learning difficulty*&bull;",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Examination feature",
"       </td>",
"       <td class=\"subtitle1\">",
"        Potential clinical implication",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        General observations and appearance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sadness, anxiety, attention span, impulsivity, activity",
"       </td>",
"       <td>",
"        Emotional or behavioral problems may affect learning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ability to communicate and respond to instructions",
"       </td>",
"       <td>",
"        Measure of language skills other than phonologic processing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Stigmata of genetic syndromes (eg, Klinefelter, Turner, fragile X)",
"       </td>",
"       <td>",
"        Klinefelter syndrome: tall stature, small testes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Turner syndrome: short stature, webbed neck, micrognathia, short metacarpals, nail dysplasia, widely spaced nipples",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fragile X syndrome: narrow face; large jaw; long, prominent ears; large testes (after puberty)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Growth parameters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Head circumference",
"       </td>",
"       <td>",
"        Head circumference &lt;10",
"        <sup>",
"         th",
"        </sup>",
"        percentile may suggest fetal alcohol syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Children with fragile X syndrome usually have head circumference &gt;50",
"        <sup>",
"         th",
"        </sup>",
"        percentile",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Height and weight",
"       </td>",
"       <td>",
"        Height and weight &lt;10",
"        <sup>",
"         th",
"        </sup>",
"        percentile may suggest fetal alcohol syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Short stature is a feature of Turner syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tall stature is a feature of Klinefelter syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Skin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Caf&eacute;-au-lait spots, ash leaf spots, adenoma sebaceum",
"       </td>",
"       <td>",
"        Neurocutaneous disorders (eg, neurofibromatosis, tuberous sclerosis) are associated with learning problems",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Genitalia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Delayed sexual maturation",
"       </td>",
"       <td>",
"        May be associated with Turner syndrome, Klinefelter syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Small testes",
"       </td>",
"       <td>",
"        Suggestive of Klinefelter syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Large testes (after puberty)",
"       </td>",
"       <td>",
"        Suggestive of fragile X syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Sensory screening",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hearing assessment",
"       </td>",
"       <td>",
"        Hearing impairment may affect learning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vision assessment",
"       </td>",
"       <td>",
"        Vision impairment may affect learning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Neurologic examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Asymmetry, increased or decreased muscle tone",
"       </td>",
"       <td>",
"        Cerebral palsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Coordination problems",
"       </td>",
"       <td>",
"        Developmental coordination disorder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Weakness",
"       </td>",
"       <td>",
"        Muscular dystrophy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tics",
"       </td>",
"       <td>",
"        Tourette syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Staring episodes",
"       </td>",
"       <td>",
"        Seizure disorder",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * In this table, \"learning disability\" refers to a heterogeneous group of disorders characterized by the unexpected failure of an individual to acquire, retrieve, and use information competently, rather than intellectual disability (mental retardation).",
"     <br>",
"      &bull; Most children with learning disability have a normal physical examination.",
"      <br>",
"       &Delta; Learning disability is rarely the first manifestation of these disorders.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
