" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Evaluation of pallor in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?12/27/12729/contributors\">",
