var title_f12_30_12781="DBA versus TEC";
var content_f12_30_12781=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F76264&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F76264&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differential diagnosis of acquired pure red cell aplasia in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diamond-Blackfan anemia",
"       </td>",
"       <td class=\"subtitle1\">",
"        Transient erythroblastopenia of childhood",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Age at diagnosis",
"        </strong>",
"       </td>",
"       <td>",
"        90 percent &lt;1 year",
"       </td>",
"       <td>",
"        80 percent from one to four years",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Male:Female",
"        </strong>",
"       </td>",
"       <td>",
"        1.1",
"       </td>",
"       <td>",
"        1.2",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Etiology",
"        </strong>",
"       </td>",
"       <td>",
"        Constitutional",
"       </td>",
"       <td>",
"        Acquired",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Antecedent history",
"        </strong>",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        Viral illness",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Body height",
"        </strong>",
"       </td>",
"       <td>",
"        50 percent below standard",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Congenital anomalies",
"        </strong>",
"       </td>",
"       <td>",
"        35 percent have one or more anomaly",
"       </td>",
"       <td>",
"        Absent",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Laboratory values",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hemoglobin",
"       </td>",
"       <td>",
"        2 to 6 g/dL at diagnosis",
"       </td>",
"       <td>",
"        3 to 9 g/dL at diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        White cell count",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Normal to decreased",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Platelet count",
"       </td>",
"       <td>",
"        Usually normal (80 percent of patients have platelets &gt;400,000/microL)",
"       </td>",
"       <td>",
"        Normal to elevated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"3\">",
"        MCV increased:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        At diagnosis",
"       </td>",
"       <td>",
"        Yes, 30 percent",
"       </td>",
"       <td>",
"        No",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        During recovery",
"       </td>",
"       <td>",
"        Yes, 100 percent",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        During remission",
"       </td>",
"       <td>",
"        Yes, 100 percent",
"       </td>",
"       <td>",
"        No",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"3\">",
"        Hemoglobin F increased:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        At diagnosis",
"       </td>",
"       <td>",
"        Yes, 100 percent",
"       </td>",
"       <td>",
"        No",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        During recovery",
"       </td>",
"       <td>",
"        Yes, 100 percent",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        During remission",
"       </td>",
"       <td>",
"        Yes, 85 percent",
"       </td>",
"       <td>",
"        No",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        eADA activity",
"       </td>",
"       <td>",
"        Elevated",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        i RBC antigen",
"       </td>",
"       <td>",
"        Present",
"       </td>",
"       <td>",
"        Absent",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Course",
"        </strong>",
"       </td>",
"       <td>",
"        Prolonged transfusion support or steroid therapy",
"       </td>",
"       <td>",
"        Spontaneous recovery within weeks to months",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MCV: mean corpuscular volume; eADA: erythrocyte adenosine deaminase; i RBC antigen: red blood cell i antigen.",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        Alter BP, Young NS. The Bone Marrow Failure Syndromes. In: Nathan and Oski's Hematology of Infancy and Childhood, Nathan DG, Orkin SH (Eds), WB Saunders, 1998, p.237.",
"       </li>",
"       <li>",
"        Oski FA, Pediatr Rev 1982; 4:25.",
"       </li>",
"       <li>",
"        Gerrits GP, van Oostrom CG, de Vaan GA, Bakkeren JA, Eur J Pediatr 1984; 142:266.",
"       </li>",
"       <li>",
"        Vlachos A, et al. Diagnosing and treating Diamond Blackfan anemia: results of an international clinical consensus conference. Br J Hematol 2008; 142:859.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
