" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pathogenesis, clinical features, and diagnosis of pediatric multiple sclerosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?12/32/12809/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?12/32/12809/contributors\">",
"     Timothy E Lotze, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?12/32/12809/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?12/32/12809/contributors\">",
"     Marc C Patterson, MD, FRACP",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?12/32/12809/contributors\">",
"     Francisco Gonzalez-Scarano, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?12/32/12809/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?12/32/12809/contributors\">",
"     John F Dashe, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
