var title_f12_32_12812="Ambulatory ECG in sudden death";
var content_f12_32_12812=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F57596&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F57596&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Ambulatory ECG monitoring findings in 157 cases of sudden cardiac death",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Etiology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Ventricular tachyarrhythmia",
"       </td>",
"       <td class=\"sublist1_other_start\">",
"        83.4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        VT/VF",
"       </td>",
"       <td class=\"sublist_other\">",
"        62.4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Primary VF",
"       </td>",
"       <td class=\"sublist_other\">",
"        8.3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Torsade polymorphic VT",
"       </td>",
"       <td class=\"sublist_other\">",
"        12.7",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bradycardia",
"       </td>",
"       <td>",
"        16.5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Electrocardiographic",
"abnormalities",
"       </td>",
"       <td class=\"subtitle1\">",
"        Finding or frequency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Increase in VPBs",
"       </td>",
"       <td>",
"        75 to 90 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Increase in complex VPBs",
"       </td>",
"       <td>",
"        70 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        R on T VPBs",
"       </td>",
"       <td>",
"        15 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Monomorphic VT duration before VF",
"       </td>",
"       <td>",
"        167 sec",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Polymorphic VT duration before VF",
"       </td>",
"       <td>",
"        34 sec",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Heart rate before VT or VF",
"       </td>",
"       <td>",
"        Accelerated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        VT cycle length before VF",
"       </td>",
"       <td>",
"        Accelerated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Ischemic ST segment changes",
"       </td>",
"       <td>",
"        13 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        QTc",
"       </td>",
"       <td>",
"        Unchanged",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     VT: ventricular tachycardia; VF: ventricular fibrillation; VPB: ventricular premature beat.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Bayes de Luna A, Coumel P, Leclerc JF. Am Heart J 1989; 117:151.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
