var title_f12_37_12893="Empiric abx purulent mening";
var content_f12_37_12893=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F71968&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F71968&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for empiric antimicrobial therapy for purulent meningitis based on patient age and specific predisposing condition*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Predisposing factor",
"      </td>",
"      <td class=\"subtitle1\">",
"       Common bacterial pathogens",
"      </td>",
"      <td class=\"subtitle1\">",
"       Antimicrobial therapy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\" colspan=\"3\">",
"       Age",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &lt;1 month",
"      </td>",
"      <td class=\"sublist_other\">",
"       <em>",
"        Streptococcus agalactiae",
"       </em>",
"       ,",
"       <em>",
"        Escherichia coli",
"       </em>",
"       ,",
"       <em>",
"        Listeria monocytogenes",
"       </em>",
"       ,",
"       <em>",
"        Klebsiella",
"       </em>",
"       species",
"      </td>",
"      <td class=\"sublist_other\">",
"       Ampicillin plus cefotaxime; OR ampicillin plus an aminoglycoside",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       1-23 months",
"      </td>",
"      <td class=\"sublist_other\">",
"       <em>",
"        Streptococcus pneumoniae",
"       </em>",
"       ,",
"       <em>",
"        Neisseria meningitidis",
"       </em>",
"       ,",
"       <em>",
"        S. agalactiae",
"       </em>",
"       ,",
"       <em>",
"        Haemophilus influenzae",
"       </em>",
"       ,",
"       <em>",
"        E. coli",
"       </em>",
"      </td>",
"      <td class=\"sublist_other\">",
"       Vancomycin plus a third-generation cephalosporin&bull;&Delta;&loz;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       2-50 years",
"      </td>",
"      <td class=\"sublist_other\">",
"       <em>",
"        N. meningitidis",
"       </em>",
"       ,",
"       <em>",
"        S. pneumoniae",
"       </em>",
"      </td>",
"      <td class=\"sublist_other\">",
"       Vancomycin plus a third-generation cephalosporin&bull;&Delta;&loz;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &gt;50 years",
"      </td>",
"      <td class=\"sublist_other\">",
"       <em>",
"        S. pneumoniae",
"       </em>",
"       ,",
"       <em>",
"        N. meningitidis",
"       </em>",
"       ,",
"       <em>",
"        L. monocytogenes",
"       </em>",
"       , aerobic gram-negative bacilli",
"      </td>",
"      <td class=\"sublist_other\">",
"       Vancomycin plus ampicillin plus a third-generation cephalosporin&bull;&Delta;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\" colspan=\"3\">",
"       Head trauma",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Basilar skull fracture",
"      </td>",
"      <td class=\"sublist_other\">",
"       <em>",
"        S. pneumoniae",
"       </em>",
"       ,",
"       <em>",
"        H. influenzae",
"       </em>",
"       , group A beta-hemolytic streptococci",
"      </td>",
"      <td class=\"sublist_other\">",
"       Vancomycin plus a third-generation cephalosporin&bull;&Delta;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Penetrating trauma",
"      </td>",
"      <td class=\"sublist_other\">",
"       <em>",
"        Staphylococcus aureus",
"       </em>",
"       , coagulase-negative staphylococci (especially",
"       <em>",
"        Staphylococcus epidermidis",
"       </em>",
"       ), aerobic gram-negative bacilli (including",
"       <em>",
"        Pseudomonas aeruginosa",
"       </em>",
"       )",
"      </td>",
"      <td class=\"sublist_other\">",
"       Vancomycin plus cefepime; OR vancomycin plus ceftazidime; OR vancomycin plus meropenem",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Postneurosurgery",
"      </td>",
"      <td>",
"       Aerobic gram-negative bacilli (including",
"       <em>",
"        P. aeruginosa",
"       </em>",
"       ),",
"       <em>",
"        S. aureus",
"       </em>",
"       , coagulase-negative staphylococci (especially",
"       <em>",
"        S. epidermidis",
"       </em>",
"       )",
"      </td>",
"      <td>",
"       Vancomycin plus cefepime; OR vancomycin plus ceftazidime; OR vancomycin plus meropenem",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Immunocompromised state",
"      </td>",
"      <td>",
"       <em>",
"        S. pneumoniae",
"       </em>",
"       ,",
"       <em>",
"        N. meningitidis",
"       </em>",
"       ,",
"       <em>",
"        L. monocytogenes",
"       </em>",
"       , aerobic gram-negative bacilli (including",
"       <em>",
"        P. aeruginosa",
"       </em>",
"       )",
"      </td>",
"      <td>",
"       Vancomycin plus ampicillin plus cefepime; OR vancomycin plus ampicillin plus meropenem",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * For recommended dosages for adults, see the table \"Recommended intravenous dosages of antimicrobial therapy for adults with bacterial meningitis\".",
"     <br>",
"      &bull; Ceftriaxone or cefotaxime.",
"      <br>",
"       &Delta; Some experts would add rifampin if dexamethasone is also given.",
"       <br>",
"        &loz; Add ampicillin if meningitis caused by Listeria monocytogenes is suspected.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Modified with permission from: Tunkel, AR, Hartman, BJ, Kaplan, SL, et al. Practice guidelines for the management of bacterial meningitis. Clin Infect Dis 2004; 39:1267. Copyright &copy;2004 University of Chicago Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
