var title_f12_45_13020="Screening von Hippel Lind";
var content_f12_45_13020=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F66320&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F66320&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for screening for von Hippel Lindau disease*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Any blood relative of an individual diagnosed with VHL disease by gene analysis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Individuals with a VHL associated lesion&bull; and a family history of VHL associated-lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Individuals with two or more VHL-associated lesions&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Individuals with any of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hemangioblastoma (HB) diagnosed at age &lt;30 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &gt;2 CNS hemangioblastomas (diagnosed at any age)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Clear cell renal carcinoma (RCC) diagnosed at age &lt;40 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Bilateral and/or multiple RCC",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Pheochromocytoma (PHEO) diagnosed at age &lt;40 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Bilateral and/or multiple PHEO",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Personal and family history of pheochromocytoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &gt;1 Pancreatic serous cystadenoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &gt;1 Pancreatic neuroendocrine tumor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Multiple pancreatic cysts + any VHL associated lesion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Middle ear endolymphatic sac tumor (unilateral or bilateral)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Epididymal papillary cystadenoma (unilateral or bilateral)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Bilateral epididymal cysts",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * As used by the Massachusetts General Hospital Center for Cancer Risk Analysis and the VHL Clinic.",
"     <br>",
"      &bull; VHL associated-lesions include any of the following: Hemangioblastoma, Clear cell renal carcinoma, Pheochromocytoma, Middle ear endolymphatic sac tumor, Epididymal papillary cystadenoma, Pancreatic serous cystadenomas (but not mucinous cystadenoma), Pancreatic neuroendocrine tumor. (Note: Simple cysts of the kidneys, pancreas or liver are not considered VHL-associated lesions.)",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
