" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pathologic skin picking",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?12/49/13080/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?12/49/13080/contributors\">",
"     Kelly KyungHwa Park, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?12/49/13080/contributors\">",
"     John Koo, MD",
