var title_f12_49_13083="Management infertility";
var content_f12_49_13083=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F73570&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F73570&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    General principles of management of infertile couples",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       Involve both partners in the evaluation and management of infertility",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Recommend lifestyle modifications to enhance fertility",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Couple - smoking cessation and reduce exposure to potential environmental toxins",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Women - abstinence from alcohol, reduction of excessive caffeine intake, weight modulation to achieve target body mass index (20-25 kg/m2)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Perform infertility evaluation according to established guidelines",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Identify causes of infertility",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Reversible causes - implement medical or surgical therapy to correct the etiology of infertility",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Irreversible causes - utilize assisted reproductive technologies, gamete donation, gestational carrier, adoption to overcome the etiology of infertility",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
