" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Dermatofibrosarcoma protuberans",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?12/50/13098/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?12/50/13098/contributors\">",
"     William M Mendenhall, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?12/50/13098/contributors\">",
"     Mark T Scarborough, MD",
