var title_f12_50_13099="Curth postulates";
var content_f12_50_13099=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F71642&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F71642&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Curth's postulates*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Postulates",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definitions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Concurrent onset",
"       </td>",
"       <td>",
"        The dermatosis and malignancy occur concurrently",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Parallel course",
"       </td>",
"       <td>",
"        If the malignancy is treated successfully or recurs, the dermatosis follows a similar course",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Uniformity",
"       </td>",
"       <td>",
"        A specific malignancy is consistently associated with a specific dermatosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Statistical significance",
"       </td>",
"       <td>",
"        There is a statistically significant association between the malignancy and the dermatosis based upon case-control studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5. Genetic basis",
"       </td>",
"       <td>",
"        There is a genetic association between the malignancy and dermatosis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Curth's postulates are criteria used to identify a consistent relationship between an internal malignancy and a cutaneous disorder. All criteria do not have to be met.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Thiers BH, Sahn RE, Callen JP. Cutaneous manifestations of internal malignancy. CA Cancer J Clin 2009; 59:73.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
