var title_f12_51_13115="Termination of exercise testing in children";
var content_f12_51_13115=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F57123&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F57123&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Indications for terminating pediatric exercise testing before reaching maximal voluntary capacity level",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        The onset of serious cardiac arrhythmias (eg, ventricular tachycardia, supraventricular tachycardia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Any appearance of potential hazard to the patient",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Failure of electrocardiographic monitoring system",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Symptoms such as pain, headache, dizziness, or syncope, precipitated by exercise",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Segmental ST depression or elevation &ge;3 mm during exercise",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Arrhythmia (over 25 percent of beats) precipitated or aggravated by exercise",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recognized types of intracardiac block precipitated by exercise",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inappropriate rise in blood pressure with systolic pressures exceeding 230 mmHg and diastolic pressures exceeding 120 mmHg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inappropriate fall in blood pressure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Marked signs of cutaneous vascular insufficiency (eg, pallor)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
