var title_f12_52_13133="MPM II";
var content_f12_52_13133=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F77798&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F77798&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Mortality prediction model II (MPM II)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Variable",
"      </td>",
"      <td class=\"subtitle1\">",
"       Response",
"      </td>",
"      <td class=\"subtitle1\">",
"       Points",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Patient age*",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Medical or unscheduled surgical admission?",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Cardiopulmonary resuscitation prior to admission?",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       <p>",
"        Coma (Glasgow coma scale 3-5)?",
"       </p>",
"       <p>",
"        (Does not include patients whose coma is due to overdose or who received neuromuscular blocking agents)",
"       </p>",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Heart rate &ge;150 bpm?",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Systolic blood pressure &le;90 mmHg?",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Mechanical ventilation?",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       <p>",
"        Acute renal failure?",
"       </p>",
"       <p>",
"        (Does not include pre-renal azotemia)",
"       </p>",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Cardiac dysrhythmias?",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Cerebrovascular accident?",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Intracranial mass effect?",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Gastrointestinal bleeding?",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       <p>",
"        Metastatic carcinoma?",
"       </p>",
"       <p>",
"        (Distant metastases only; does not include local lymph node involvement)",
"       </p>",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Cirrhosis?",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       <p>",
"        Chronic renal insufficiency?",
"       </p>",
"       <p>",
"        (Creatinine &gt;2 mg/dL chronically)",
"       </p>",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       0",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Patient age does not receive points when calculating the severity score; however, it is used in the formula to calculate predicted mortality.",
"     <br>",
"      &bull; Excluded patients include the &lt;18 years old, burn patients, coronary care patients, and cardiac surgery patients.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Lemeshow, S, Teres, D, Klar, J, et al. Mortality probability models (MPM II) based on an international cohort of intensive care unit patients. JAMA 1993; 270:2478.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
