var title_f12_55_13181="Guidance for PrEP against HIV";
var content_f12_55_13181=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F52982&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F52982&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation and monitoring of patients prior to initiation and during administration of pre-exposure prophylaxis against HIV",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Before initiating PrEP",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Determine eligibility",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Document negative HIV antibody test(s) immediately before starting PrEP medication.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Test for acute HIV infection if patient has symptoms consistent with acute HIV infection.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Confirm that patient is at substantial, ongoing, high risk for acquiring HIV infection.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Confirm that calculated creatinine clearance is &ge;60 mL per minute (via Cockcroft-Gault formula).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Other recommended actions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Screen for hepatitis B infection; vaccinate against hepatitis B if susceptible, or treat if active infection exists, regardless of decision about prescribing PrEP.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Screen and treat as needed for STIs.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Perform pregnancy testing&nbsp;for women.&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Beginning PrEP medication regimen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span class=\"indent1\">",
"         Prescribe 1 tablet of Truvada (TDF [300 mg] plus FTC [200 mg]) daily.",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span class=\"indent1\">",
"         In general, prescribe no more than a 90-day supply, renewable only after HIV testing confirms that patient remains HIV-uninfected.",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span class=\"indent1\">",
"         If active hepatitis B infection is diagnosed, consider using TDF/FTC for both treatment of active hepatitis B infection and HIV prevention.",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span class=\"indent1\">",
"         Provide risk-reduction and PrEP medication adherence counseling and condoms.",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Follow-up while PrEP medication is being taken",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Every&nbsp;two to three&nbsp;months, perform an HIV antibody test; document negative result.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evaluate and support PrEP medication adherence at each follow-up visit, more often if inconsistent adherence is identified.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Every&nbsp;two to three&nbsp;months, assess risk behaviors and provide risk-reduction counseling and condoms. Assess STI symptoms and, if present, test and treat for STI as needed.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Every&nbsp;six months, test for STI even if patient is asymptomatic, and treat as needed.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Every three months, check blood urea nitrogen and serum creatinine. Discontinue PrEP if creatinine clearance decreases by 20 percent or to &lt;50 mL per minute.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Every&nbsp;three months, assess pregnancy status for women on&nbsp;PreP&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        On discontinuing PrEP (at patient request, for safety concerns, or if HIV infection is acquired)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform HIV test(s) to confirm whether HIV infection has occurred.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If HIV-positive, order and document results of resistance testing and establish linkage to HIV care.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If HIV-negative, establish linkage to risk-reduction support services as indicated.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If active hepatitis B is diagnosed at initiation of PrEP, consider appropriate medication for continued treatment of hepatitis B.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <p>",
"     HIV: human immunodeficiency virus; PrEP: Pre-exposure prophylaxis; STI: sexually transmitted infection; TDF: tenofovir disoproxil fumarate; FTC: emtricitabine.&nbsp;",
"    </p>",
"    <div class=\"footnotes\">",
"     <br/>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted&nbsp;from:",
"     <br>",
"      <ol>",
"       <li>",
"        CDC. Interim guidance: preexposure prophylaxis for the prevention of HIV infection in men who have sex with men. MMWR 2011;60:65&ndash;8.",
"       </li>",
"       <li>",
"        CDC. Interim Guidance for Clinicians Considering the Use of Preexposure Prophylaxis for the Prevention of HIV Infection in Heterosexually Active Adults. MMWR 2012; 61:586-589",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
