var title_f12_57_13212="DSM IV criteria Asperger disorder";
var content_f12_57_13212=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F60599&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F60599&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for Asperger disorder",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        A. Qualitative impairment in social interaction, as manifested by at least two of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        1. Marked impairments in the use of multiple nonverbal behaviors such as eye-to-eye gaze, facial expression, body postures, and gestures to regulate social interaction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        2. Failure to develop peer relationships appropriate to developmental level",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        3. A lack of spontaneous seeking to share enjoyment, interests, or achievements with other people (eg, by a lack of showing, bringing, or pointing out objects of interest to other people)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        4. Lack of social or emotional reciprocity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        B. Restricted repetitive and stereotyped patterns of behavior, interests, and activities, as manifested by at least one of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        1. Encompassing preoccupation with one or more stereotyped and restricted patterns of interest that are abnormal either in intensity or focus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        2. Apparently inflexible adherence to specific, nonfunctional routines or rituals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        3. Stereotyped and repetitive motor mannerisms (eg, hand or finger flapping or twisting, or complex whole-body movements)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        4. Persistent preoccupation with parts of objects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C. The disturbance causes clinically significant impairment in social, occupational, or other important areas of functioning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        D. There is no clinically significant general delay in language (eg, single words used by age two years, communicative phrases used by age three years)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E. There is no clinically significant delay in cognitive development or in the development of age-appropriate self-help skills, adaptive behavior (other than social interaction), and curiosity about the environment in childhood",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        F. Criteria are not met for another specific pervasive developmental disorder or schizophrenia",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reprinted with permission from: the Diagnostic and Statistical Manual of Mental Disorders, Fourth Edition, Text Revision (Copyright 2000). American Psychiatric Association.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
