var title_f12_58_13228="Diagnostic criteria cluster headache";
var content_f12_58_13228=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F80843&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F80843&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ICHD-2 diagnostic criteria for cluster headache",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        A. At least five headache attacks fulfilling criteria B through D:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B. Severe or very severe unilateral orbital, supraorbital, and/or temporal headache attacks, which last untreated for 15 to 180 minutes. During part (but less than half) of the time course of the cluster headache, attacks may be less severe, less frequent, or of shorter or longer duration.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        C. The headache is accompanied by at least one of the following symptoms:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        1. Ipsilateral conjunctival injection or lacrimation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        2. Ipsilateral nasal congestion and/or rhinorrhea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        3. Ipsilateral eyelid edema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        4. Ipsilateral forehead and facial sweating",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        5. Ipsilateral miosis and/or ptosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        6. A sense of restlessness and agitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        D. The attacks have a frequency from one every other day to eight per day.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E. History and physical and neurologic examinations do not suggest any other disorder, and/or such a disorder is ruled out by appropriate investigations, or such disorder is present but attacks do not occur for the first time in close temporal relation to the disorder.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Episodic cluster headache:",
"        </strong>",
"        At least two cluster periods lasting seven days to one year separated by pain-free periods lasting one month or longer.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Chronic cluster headache:",
"        </strong>",
"        Attacks occur for more than one year without remission or with remission less than one month.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Probable cluster headache:",
"        </strong>",
"        Attacks fulfilling all but one criteria for cluster headache.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Headache Classification Subcommittee of the International Headache Society. The International Classification of Headache Disorders: 2nd edition. Cephalalgia 2004; 24 Suppl 1:9.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
