var title_f12_7_12411="Exercise ECG in aortic stenosis";
var content_f12_7_12411=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F62494&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F62494&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for a positive exercise test in asymptomatic patients with aortic stenosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Development of dyspnea, angina, or presyncope or syncope, which should lead to stopping of the test",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A rise in systolic pressure during exercise of less than 20 mmHg; the test is stopped if the systolic pressure falls more than 10 mmHg with exercise",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inability to reach 80 percent of the predicted normal level of exercise tolerance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        More than 2 mm horizontal or downsloping ST segment depression compared to baseline that cannot be attributed to other causes; the test is stopped if there is more than 5 mm ST segment depression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The development of complex ventricular arrhythmias, such as ventricular tachycardia or more than four ventricular premature beats in succession, which should lead to stopping of the test",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Otto CM, Burwash IG, Legget ME, et al. Circulation 1997; 95:2262; and from Iung B, Gohlke-Barwolf C, Tornos P, et al. Eur Heart J 2002; 23:1253.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
