var title_f12_7_12412="Pharma Rx opioid sd eff";
var content_f12_7_12412=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F75786&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F75786&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Commonly used pharmacologic approaches in the management of opioid side effects",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Side effect",
"      </td>",
"      <td class=\"subtitle1\">",
"       Treatment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"11\">",
"       Constipation",
"      </td>",
"      <td class=\"sublist1_start\">",
"       General approach",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Ensure comfort and convenience for defecation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Rule out impaction",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Improve hydration",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Avoid adding fiber if the patient is debilitated or nutrition or hydration is poor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Pharmacologic approaches",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Periodic enemas",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Oral therapy usually starts with stimulant laxative (eg, senna), stool softener (docusate), and/or an osmotic laxative (eg, lactulose or propylethylene glycol)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; For refractory cases, the initial step is to adjust the doses or change the combination of the usual starting regimen, or add more rapid acting osmotic drug (eg, magnesium citrate)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; For refractory cases, consider second-line approaches (eg, metoclopramide, lubiprostone, tegaserod, colchicine, oral naloxone)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Peripherally-acting opioid antagonist drugs (methylnaltrexone) an option for refractory cases",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"6\">",
"       Somnolence or cognitive impairment",
"      </td>",
"      <td class=\"sublist1_start\">",
"       General approach",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Treatment of potential etiologies",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Reduce opioid dose by 25 to 50 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Pharmacologic approaches",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Add psychostimulant (eg, methylphenidate, modafinil)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Consider cholinesterase inhibitor (eg, donezepil)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
