var title_f12_9_12444="Levels of dyspnea in ILD";
var content_f12_9_12444=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F55810&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F55810&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Levels of dyspnea",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Level of dyspnea",
"       </td>",
"       <td class=\"subtitle1\">",
"        Number of points",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        None (or same as peers) even after 30 min of vigorous",
"activity, such as running; ability to lift and carry 27 kg (60 lbs) for",
"a prolonged period of time",
"       </td>",
"       <td>",
"        0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        After 5 flights of stairs or 10 min of vigorous",
"activity/prolonged use of heavy tools",
"       </td>",
"       <td>",
"        2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        After walking more than a mile (1609 m) on level ground",
"or up three flights of stairs; less than 10 min of vigorous activity,",
"such as running or tennis",
"       </td>",
"       <td>",
"        4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Upon walking 0.25 to 1 mile (402 to 1604 m) on level",
"ground or up 2 flights of stairs; dyspnea with paper hanging",
"       </td>",
"       <td>",
"        6",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Upon walking 300 to 1,320 feet (91.4 to 402.3 m) on",
"level ground; bed-making*",
"       </td>",
"       <td>",
"        8",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Upon walking 150 to 300 feet (45.7 to 91.4 m) on level",
"ground or up 1 flight of stairs; scrubbing; truck driving;",
"assembly-line work&bull;",
"       </td>",
"       <td>",
"        10",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Upon walking 50 to 150 feet (15.2 to 45.7 m) on level",
"ground at approximately 3 mph; light janitorial work&bull;",
"       </td>",
"       <td>",
"        12",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Upon walking 20 to 50 feet (6.1 to 15.2 m) on level",
"ground; light steady work at one's own pace; seated operation of heavy",
"equipment&bull;",
"       </td>",
"       <td>",
"        14",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        With minor exertion, such as dressing, walking less",
"than 20 feet (6.1 meters), or prolonged talking&bull;",
"       </td>",
"       <td>",
"        16",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        With minimal activity, such as eating, defecating,",
"writing, sitting up&bull;",
"       </td>",
"       <td>",
"        18",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        At rest&bull;",
"       </td>",
"       <td>",
"        20",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Defined as moderately severe breathlessness.",
"     <br>",
"      &bull; Defined as severe breathlessness.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Redrawn from: Watters, LC, King, TE, Schwarz, MI, et al, Am Rev Respir Dis 1986; 133:97.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
