" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pasteurella infections",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?13/0/13321/contributors\">",
