var title_f13_0_13324="DTP adverse reaction rates";
var content_f13_0_13324=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F52093&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F52093&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Estimated rates of moderate and severe adverse reactions after diphtheria-tetanus-whole cell pertussis (DTwP) and diphtheria-tetanus-acellular pertussis (DTaP) vaccines",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Adverse reaction",
"       </td>",
"       <td class=\"subtitle1\">",
"        DTwP",
"       </td>",
"       <td class=\"subtitle1\">",
"        DTaP",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Entire limb swelling",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        1 in 33 to 1 in 50 children after the fourth or fifth dose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persistent inconsolable crying (&ge;3 hours)",
"       </td>",
"       <td>",
"        1 in 100 doses",
"       </td>",
"       <td>",
"        1 in 1000 children",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypotonic-hyporesponsive episode (collapse, shock-like state)",
"       </td>",
"       <td>",
"        1 in 1750 doses (range 1 in 344 to 1 in 28,570)",
"       </td>",
"       <td>",
"        Less than 1 per 10,000 children (range 1 in 714 to 1 in 25,000)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Seizures (febrile)",
"       </td>",
"       <td>",
"        1 in 1750 doses",
"       </td>",
"       <td>",
"        1 in 14,000 children",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fever &ge;105&deg;F (40.5&deg;C)",
"       </td>",
"       <td>",
"        1 in 333 doses",
"       </td>",
"       <td>",
"        1 in 16,000 children",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anaphylaxis",
"       </td>",
"       <td>",
"        1 in 50,000 doses",
"       </td>",
"       <td>",
"        Less than 1 in 1 million doses",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     <p>",
"      &nbsp;",
"     </p>",
"    </div>",
"    <div class=\"reference\">",
"     1. American Academy of Pediatrics. Pertussis. In: Red Book: 2012 Report of the Committee on Infectious Diseases, 29th ed, Pickering LK (Ed), American Academy of Pediatrics, Elk Grove Village, IL 2012. p.553.",
"     <br>",
"      2. US Department of Health and Human Services. Centers for Disease Control and Prevention. Vaccine Information Statement DTaP (5/17/07). Available at: www.cdc.gov/vaccines/pubs/vis/default.htm#dtap. Accessed on June 25, 2012.",
"      <br>",
"       3.&nbsp;Centers for Disease Control and Prevention. Pertussis. In: Epidemiology and Prevention of Vaccine-Preventable Diseases. The Pink book, 12th ed. Atkinson W, Wolfe S, Hamborsky J (Eds). Public Health Foundation, Washington, DC 2012. Available at: www.cdc.gov/vaccines/pubs/pinkbook/index.html.&nbsp;Accessed on June 25, 2012.",
"       <br>",
"        4. &nbsp;Gold MS. Hypotonic-hyporesponsive episodes following pertussis vaccination: a cause for concern?. Drug Saf 2002; 25:85.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
