var title_f13_1_13339="Etiology of priapism";
var content_f13_1_13339=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F54354&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F54354&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    The etiology of priapism",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       1-Primary (idiopathic)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       2-Secondary",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Hematologic (sickle cell anemia, leukemia, thalassemia, multiple myeloma, thrombotic thrombocytopenic purpura)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Neurologic (spinal shock)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Tumors (metastatic cancers)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Perineal, pelvic or penile trauma",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Iatrogenic (intracavernous injections)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Drugs (anticoagulants, anti-hypertensives, anti-depressants, PDE5 inhibitors, intracavernous injections, alpha-blockers, cocaine)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Infection (malaria, spider toxins)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Metabolic disorders (gout, hemodialysis, high lipid content, total parenteral nutrition, diabetes, amyloidosis)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
