"  <h1>",
"   TOPIC OUTLINE",
"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li>",
"     <a class=\"sr_button\" href=\"#H30\" id=\"summRecButton\">",
"      <span>",
"       SUMMARY",
"      </span>",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H1\">",
"      INTRODUCTION",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H2\">",
"      CAUSES",
"     </a>",
"    </li>",
"    <li class=\"bulletItem\">",
"     <a class=\"outlineLink\" href=\"#H3\">",
"      Life-threatening conditions",
"     </a>",
