var title_f13_13_13531="Melatonin timing";
var content_f13_13_13531=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F50327&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F50327&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Timing of melatonin intake",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Direction of travel",
"       </td>",
"       <td class=\"subtitle1\">",
"        During travel*",
"       </td>",
"       <td class=\"subtitle1\">",
"        1st evening at destination",
"       </td>",
"       <td class=\"subtitle1\">",
"        2nd",
"       </td>",
"       <td class=\"subtitle1\">",
"        3rd",
"       </td>",
"       <td class=\"subtitle1\">",
"        4th",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         <strong>",
"          Eastward",
"         </strong>",
"        </p>",
"        <p>",
"         eg, US to Europe",
"        </p>",
"       </td>",
"       <td>",
"        After it gets dark, 30 min before target bedtime",
"       </td>",
"       <td colspan=\"4\">",
"        After dark, 30 min before local bedtime",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         <strong>",
"          Westward",
"         </strong>",
"        </p>",
"        <p>",
"         eg, Europe to US",
"        </p>",
"        <p>",
"         US to Australia",
"        </p>",
"       </td>",
"       <td>",
"        [Not needed]",
"       </td>",
"       <td colspan=\"4\">",
"        After dark, 30 min before local bedtime",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * If travel involves more than one night, take one dose on each travel night.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
