var title_f13_13_13533="Differential dx COPD";
var content_f13_13_13533=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F51974&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F51974&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differential diagnosis of COPD",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Diagnosis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Suggestive Features*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"5\">",
"        COPD",
"       </td>",
"       <td>",
"        Onset in mid-life; onset in early adulthood should prompt suspicion for alpha-1 antitrypsin deficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Symptoms slowly progressive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Long smoking history, although can occur in nonsmokers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dyspnea during exercise",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Largely irreversible airflow limitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"6\">",
"        Asthma",
"       </td>",
"       <td>",
"        Onset early in life (often childhood)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Symptoms vary from day to day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Symptoms at night/early morning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Allergy, rhinitis, and/or eczema also present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Family history of asthma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Largely reversible airflow limitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        Central airway obstruction (eg, bronchogenic or metastatic cancer, lymphadenopathy, scarring from&nbsp;endotracheal tube)&nbsp;",
"       </td>",
"       <td>",
"        Monophonic wheeze or stridor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Variable inspiratory or fixed slowing on flow volume loop",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chest radiograph often normal&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Airway narrowing on three dimensional reconstruction of HRCT scan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Heart failure",
"       </td>",
"       <td>",
"        Fine basilar crackles on auscultation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chest&nbsp;radiograph shows dilated heart, pulmonary edema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary function tests typically&nbsp;indicate volume restriction,&nbsp;but airflow limitation can sometimes be seen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        Bronchiectasis",
"       </td>",
"       <td>",
"        Large volumes of purulent sputum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Commonly associated with recurrent or persistent bacterial infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Coarse crackles on auscultation, clubbing of digits",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chest radiograph/HRCT shows bronchial dilation, bronchial wall thickening",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        Tuberculosis",
"       </td>",
"       <td>",
"        Onset all ages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chest&nbsp;radiograph shows upper lung zone scarring and/or calcified granulomata",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Positive PPD or IGRA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High local prevalence of tuberculosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Obliterative bronchiolitis",
"       </td>",
"       <td>",
"        Onset in younger age, nonsmokers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May have history of rheumatoid arthritis or fume exposure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HRCT on expiration shows hypodense areas, mosaic pattern",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        Diffuse panbronchiolitis",
"       </td>",
"       <td>",
"        Most patients are male and nonsmokers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Highest prevalence in East Asia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Almost all have chronic sinusitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chest&nbsp;radiograph and HRCT show diffuse small centrilobular nodular opacities and hyperinflation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HRCT: high resolution computed tomography; PPD: purified protein derivative; IGRA: interferon gamma release assay.",
"     <br>",
"      * These features tend to be characteristic of the respective diseases, but do not occur in every case. For example, a person who has never smoked may develop COPD (especially in the developing world, where other risk factors may be more important than cigarette smoking); asthma may develop in adult and even elderly patients.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted&nbsp;with permission from the Global Initiative for Chronic Obstructive Pulmonary Disease. Global strategy for the diagnosis, management, and prevention of chronic obstructive pulmonary disease: Revised 2011. Global Initiative for Chronic Obstructive Lung Disease (GOLD), www.goldcopd.org (Accessed on August 10, 2012).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f13_13_13533=[""].join("\n");
