var title_f13_13_13534="Trauma secondary survey";
var content_f13_13_13534=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F65952&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F65952&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Secondary survey physical examination in the pediatric trauma patient",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tr>",
"      <td class=\"container\">",
"       <table cellspacing=\"0\">",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Head",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Inspect and palpate face and scalp",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Eye examination",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Visual acuity (if alert)",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Pupillary size",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Hemorrhage (conjunctiva, fundi)",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Penetrating injury",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Contact lenses (remove if indicated)",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Ocular entrapment",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Periorbital ecchymosis (Raccoon eyes)",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Ear examination",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Hemotympanum, bleeding, perforation",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Retro-auricular ecchymosis (Battle's sign)",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Midface examination",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Inspect for nasal swelling, deformity, bleeding, or clear rhinorrhea",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Inspect for oral trauma, malocclusion, intraoral lacerations",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Palpate maxilla for evidence of LeFort fracture (palatal instability with anterior to posterior pressure on the upper teeth)",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Cervical spine and neck",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          If maxillofacial/head trauma sustained, presume unstable cervical spine injury and immobilize neck until c-spine adequately evaluated",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Inspect and palpate neck",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Tracheal deviation",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Distended neck veins",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          C-spine tenderness",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Subcutaneous emphysema",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Crepitus over larynx (concerning for laryngeal fracture)",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Carotid artery bruits",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Active range of motion (in awake, cooperative patient without distracting injuries)",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Penetrating neck injury",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Inspect to determine if injury may be through the platysma",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Chest",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Inspect and palpate chest, including the clavicle, ribs, and sternum",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Open pneumothorax/large flail segment",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Crepitus",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Tenderness/bony deformity",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Auscultate",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Breath sounds",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Heart sounds",
"         </td>",
"        </tr>",
"       </table>",
"      </td>",
"      <td class=\"container\">",
"       <table cellspacing=\"0\">",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Abdomen",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Inspect and palpate abdomen",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Seat belt sign/bruises (Cullen's sign)",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Distention",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Focal tenderness",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Peritoneal signs",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Masses",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Auscultate for bowel sounds",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Perineum/rectum/vagina",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Inspection",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Contusions/lacerations/hematomas",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Males-urethral bleeding",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Females-vaginal bleeding",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Rectal examination",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          May be performed at the end of spinal examination when the body is already turned onto the side",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Assess for blood in bowel lumen",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Sphincter tone",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Males (teenage/adult)--palpate for high-riding prostate",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Muskuloskeletal",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Inspect and palpate extremities",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Identify fractures/dislocations",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Assess pulses for presence and strength",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Inspect and palpate pelvis",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Pain on palpation of pelvic ring",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Mobility of pelvis with anterior-to-posterior pressure with heels of hands on both anterior iliac spines",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Logroll patient to inspect and palpate back",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Examine for step-offs or focal tenderness",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Inspect under the axillae",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Neurologic",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist1_start\">",
"          Comprehensive neurological examination",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Re-evaluation of patient's level of consiousness",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Re-evaluation of patient's pupillary size",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Motor evaluation",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"sublist2\">",
"          Sensory evaluation",
"         </td>",
"        </tr>",
"       </table>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     1. American College of Surgeons Committee on Trauma. Advanced Trauma Life Support for Doctors, 8th ed. Chicago, IL: American College of Surgeons; 2008.",
"     <br>",
"      2. Jaffe D, Wesson D. Emergency management of blunt trauma in children.&nbsp;N Engl J Med&nbsp;1991; 324:1477-1483.",
"      <br>",
"       3. Waltzman, ML, Mooney, DP. Major Trauma. In: Fleisher, GR, Ludwig, S, Henretig, FM, eds. Textbook of Pediatric Emergency Medicine. 5th ed. Philadelphia, PA Lippincott Williams &amp; Wilkins; 2006.",
"      </br>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
