var title_f13_17_13596="Aneurysm risk in CN3 palsy";
var content_f13_17_13596=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F76645&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F76645&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk of aneurysm based on internal and external dysfunction of the third nerve",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Internal and external dysfunction",
"       </td>",
"       <td class=\"subtitle1\">",
"        Risk of aneurysm",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommendation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Complete internal dysfunction (\"pupil blown\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        With complete external dysfunction",
"       </td>",
"       <td>",
"        Highest",
"       </td>",
"       <td>",
"        MRI/MRA (CTA) catheter angiogram",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        With incomplete external dysfunction",
"       </td>",
"       <td>",
"        Highest",
"       </td>",
"       <td>",
"        MRI/MRA (CTA) catheter angiogram",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Without external dysfunction",
"       </td>",
"       <td>",
"        Minimal",
"       </td>",
"       <td>",
"        No imaging",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Incomplete internal dysfunction (\"partial pupil\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        With complete external dysfunction",
"       </td>",
"       <td>",
"        Uncertain, probably low",
"       </td>",
"       <td>",
"        MRI/MRA (CTA) consider catheter angiogram",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        With incomplete external dysfunction",
"       </td>",
"       <td>",
"        Uncertain, minimal",
"       </td>",
"       <td>",
"        MRI/MRA (CTA) consider catheter angiogram",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Without external dysfunction",
"       </td>",
"       <td>",
"        Minimal",
"       </td>",
"       <td>",
"        No imaging",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        No internal dysfunction (\"pupil not involved\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        With complete external dysfunction",
"       </td>",
"       <td>",
"        Low if isolated",
"       </td>",
"       <td>",
"        Observe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        With incomplete external dysfunction",
"       </td>",
"       <td>",
"        Uncertain",
"       </td>",
"       <td>",
"        MRI/MRA (CTA) consider catheter angiogram",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MRA: magnetic resonance angiography; MRI: magnetic resonance imaging; CTA: computed tomography angiography.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
