" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Bisphosphonates and denosumab in patients with metastatic cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?13/18/13608/contributors\">",
"     Author",
"    </a>",
