" <div id=\"innerOutline\">",
"  <h1>",
"   TOPIC OUTLINE",
"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F190953\">",
"      Brand Names: U.S.",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F190963\">",
"      Pharmacologic Category",
"     </a>",
"    </li>",
