" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ENETS staging system for pancreatic neuroendocrine tumors",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        TNM",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        T - primary tumor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\">",
"        <em>",
"         For any T, add (m) for multiple tumors",
"        </em>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        TX",
"       </td>",
"       <td colspan=\"3\">",
"        Primary tumor cannot be assessed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        T0",
"       </td>",
"       <td colspan=\"3\">",
"        No evidence of primary tumor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        T1",
"       </td>",
"       <td colspan=\"3\">",
"        Tumor limited to pancreas and size &lt;2 cm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        T2",
"       </td>",
"       <td colspan=\"3\">",
"        Tumor limited to the pancreas and size 2-4 cm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        T3",
"       </td>",
"       <td colspan=\"3\">",
"        Tumor limited to the pancreas and size &gt;4 cm or invading duodenum or bile duct",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        T4",
"       </td>",
"       <td colspan=\"3\">",
"        Tumor invading adjacent organs (stomach, spleen, colon, adrenal gland) or the wall of large vessels (celiac axis or superior mesenteric artery)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        N - regional lymph nodes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        NX",
"       </td>",
"       <td colspan=\"3\">",
"        Regional lymph node cannot be assessed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        N0",
"       </td>",
"       <td colspan=\"3\">",
"        No regional lymph node metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        N1",
"       </td>",
"       <td colspan=\"3\">",
"        Regional lymph node metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        M - distant metastases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        MX",
"       </td>",
"       <td colspan=\"3\">",
"        Distant metastasis cannot be assessed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        M0",
"       </td>",
"       <td colspan=\"3\">",
"        No distant metastases",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td class=\"indent1\">",
"        M1",
"       </td>",
"       <td colspan=\"3\">",
"        Distant metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        Stage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Disease stages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stage I",
"       </td>",
"       <td class=\"centered\">",
"        T1",
"       </td>",
"       <td class=\"centered\">",
"        N0",
"       </td>",
"       <td class=\"centered\">",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stage IIa",
"       </td>",
"       <td class=\"centered\">",
"        T2",
"       </td>",
"       <td class=\"centered\">",
"        N0",
"       </td>",
"       <td class=\"centered\">",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stage IIb",
"       </td>",
"       <td class=\"centered\">",
"        T3",
"       </td>",
"       <td class=\"centered\">",
"        N0",
"       </td>",
"       <td class=\"centered\">",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stage IIIa",
"       </td>",
"       <td class=\"centered\">",
"        T4",
"       </td>",
"       <td class=\"centered\">",
"        N0",
"       </td>",
"       <td class=\"centered\">",
"        M0",
