" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment and prognosis of basal cell carcinoma",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?13/23/13690/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?13/23/13690/contributors\">",
