var title_f13_23_13692="Types of allergic reactions";
var content_f13_23_13692=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F71375&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F71375&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of allergic reactions (Gell and Coombs)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         I",
"        </p>",
"        <p>",
"         Immediate reaction (30 to 60 min)",
"        </p>",
"        <p>",
"         Accelerated reaction (1 to 72 hours)",
"        </p>",
"       </td>",
"       <td>",
"        Anaphylactic, immediate-type hypersensitivity",
"       </td>",
"       <td>",
"        Antigen exposure causes release of vasoactive substances, such as histamine, prostaglandins, and leukotrienes from mast cells or basophils. This response is usually but not always IgE-dependent.",
"       </td>",
"       <td>",
"        <p>",
"         Anaphylaxis",
"        </p>",
"        <p>",
"         Angioedema",
"        </p>",
"        <p>",
"         Bronchospasm",
"        </p>",
"        <p>",
"         Urticaria (hives)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        II",
"       </td>",
"       <td>",
"        Antibody-dependent cytotoxicity",
"       </td>",
"       <td>",
"        An antigen or hapten that is intimately associated with a cell binds to antibody, leading to cell or tissue injury",
"       </td>",
"       <td>",
"        <p>",
"         Hemolytic anemia",
"        </p>",
"        <p>",
"         Interstitial nephritis",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        III",
"       </td>",
"       <td>",
"        Immune complex disease",
"       </td>",
"       <td>",
"        Damage is caused by formation or deposition of antigen-antibody complexes in vessels or tissue",
"       </td>",
"       <td>",
"        Serum sickness",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        IV",
"       </td>",
"       <td>",
"        Cell-mediated or delayed hypersensitivity",
"       </td>",
"       <td>",
"        Antigen exposure sensitizes T cells, which then mediate tissue injury",
"       </td>",
"       <td>",
"        Contact dermatitis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         V",
"        </p>",
"        <p>",
"         (&gt;72 hours)",
"        </p>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Uncertain, but probably involving T cell cytotoxicity",
"       </td>",
"       <td>",
"        Maculopapular rash",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Weiss ME, Adkinson NF. Immediate hypersensitivity reactions to penicillin and related antibiotics. Clin Allergy 1988; 18:515.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
